var InstitutionFinderCookie = {
		
	cookieName : "recentInstitutions",
	
	remove : function (accountId){
		var cookieValue = unescape(Cookies.read(this.cookieName));
		if (cookieValue != "null") {
			var newCookieValues = [];
			var values = cookieValue.split(',');
			for (var index = 0; index < values.length; index++) {
				if (accountId != values[index]) {
					newCookieValues[newCookieValues.length] = values[index];
				}
			}
			if (newCookieValues.length > 0) {
				Cookies.create(this.cookieName, escape(newCookieValues.join(',')), 365);
			} else {
				Cookies.remove(this.cookieName);
			}
		}
		return true;
	},

	anyInstitutions : function () {
		var cookieValue = unescape(Cookies.read(this.cookieName));
		if (Cookies.read(this.cookieName) != null) {
			return true;
		}
		return false;
	}, 
	
	add : function (accountId){
		var cookieValue = unescape(Cookies.read(this.cookieName));
		var expanded = false;
		if (cookieValue != "null") {
			var values = cookieValue.split(',');
			for (var index = 0; index < values.length; index++) {
				if (accountId == values[index]) {
					expanded = true;
				}
			}
			if (! expanded) {
				cookieValue += ","+ accountId;
			}
		} else {
			cookieValue = accountId;
		}
		Cookies.create(this.cookieName, escape(cookieValue), 365);
		return true;
	}
	
};